import React, { useState, useEffect } from "react";
import Button from '@material-ui/core/Button';
import axios from "axios";
import GoogleLogin from 'react-google-login'
import { useAppContext } from "../libs/contextLib";
import { useHistory } from "react-router-dom";
import { DataGrid,GridRowsProp, GridColDef } from '@material-ui/data-grid';

const mentorColumns= [
    { field: 'id', headerName: 'Id', width: 200 },
    { field: 'col2', headerName: 'Mentee Name', width: 200 },
    { field: 'col3', headerName: 'Status', width: 200 },

  ];

  const menteeColumns= [
    { field: 'id', headerName: 'Id', width: 200 },
    { field: 'col2', headerName: 'Mentor Name', width: 200 },
    { field: 'col3', headerName: 'Status', width: 200 },

  ];


export default function UserDashboard(){
    const [menteeRows,setMenteeRows]=useState([]);
    const [mentorRows,setMentorRows]=useState([]);

    const history = useHistory();

    function Logout(){
    localStorage.setItem("isAuthenticated","false");
       history.push("/login");
       alert("logout");
    }

    function goApplyPage() {
        history.push("/ApplyToBeMentor")
    }

    function goSearchPage() {
        history.push("/SearchPage")
    }

    useEffect(() => {
        username = localStorage.getItem('username')
        var credentials = localStorage.getItem('credentials');

       axios.get("http://localhost:8080/api/mentorship/get",username,{headers:{
        'Authorization': 'Basic ' + credentials,
        'Content-Type': 'application/json'
      }}).then(response => {
          setMenteeRows(response.mentees)
          setMentorRows(response.mentors)
      })
    }, [])

    return(
        <div className="UserDashboard">
<h1> Mentor List </h1>
<DataGrid
  rows={mentorRows}
  columns={mentorColumns}
  pageSize={10}
  />

<DataGrid
  rows={menteeRows}
  columns={menteeColumns}
  pageSize={10}
  />


<Button onClick = {goSearchPage}>
    Search Page
</Button>
<Button onClick = {goApplyPage}>
    apply to be mentor
</Button>
<Button onClick = {Logout}>
Logout
</Button>
</div>
        )
    }
