import React, { useState, useEffect } from "react";
import Button from '@material-ui/core/Button';
import axios from "axios";
import GoogleLogin from 'react-google-login'
import { useAppContext } from "../libs/contextLib";
import { useHistory } from "react-router-dom";
import MultiSelect from "react-multi-select-component";
import { DataGrid,GridRowsProp, GridColDef } from '@material-ui/data-grid';

const columns= [
    { field: 'col1', headerName: 'Main Topic', width: 150 },
    { field: 'col2', headerName: 'Sub Topics', width: 150 },
  ];

export default function SearchPage(){
    const [rows,setRows]=useState([{ id: 5, col1: " ", col2: " " }]);

    const initialValue = [
        { id: 0,title:"dsa", subTitle: "---SELECT-OPTION----" }];

    const history = useHistory();
    const [stateOptions, setStateValues] = useState(initialValue);
    const [optionsArr, setOptions] = useState(initialValue);
    const [titleId, setTitle] = useState("");
    const [subTitleId, setSubTitle] = useState([]);
    const [subT,setSubT]=useState("")

    const findMentorWithSubt = () => {
        console.log(subT);
        var auth = JSON.parse(localStorage.getItem('auth'));
        const config = { headers: {'Content-Type': 'application/json'} };
        const url = "http://localhost:8080/api/mentee/elasticsearch/subt/" + subT;
        console.log(subT);
        axios.get(url,auth).then(response => {
            console.log(response.data[0].id);
            setRows(response.data.map(application => {return{id: application.id, col2: application.menteeName, col3:application.mainTopic,col4:application.subTopic,col5:application.thoughts}}));
        }
    );  


    };



    useEffect(()  => {
        var auth = JSON.parse(localStorage.getItem('auth'));
       axios.get("http://localhost:8080/api/topic/",auth).then(response => {
                setStateValues(response.data);
                console.log(response.data)

          }
        );  
      }, []);
    useEffect(()  => {
        if(stateOptions.find(title => title.title === titleId) === undefined)
        {
            console.log("undefined");
        }else{
         setOptions(stateOptions.find(title => title.title === titleId).subTitle.map( (response) => {
             return {label : response, value: response}}))
             }    
             console.log(optionsArr)
        }, [titleId]);


    return(
        <div className="SearchPage">
            <h1> searchPage </h1>
            <select onChange={(e) => setTitle(e.target.value)}>
                {stateOptions.map(topic => <option key = {topic.id}>{topic.title}</option> )}
            </select> 
            <select onChange={(e) => setSubT(e.target.value)}>
                {optionsArr.map(topic => <option >{topic.label}</option> )}
            </select> 

            <Button onClick = {findMentorWithSubt}>Search</Button>

            <div className="AdminTopicPanel" style={{ height: 300, width: '100%' }}>
            <DataGrid
  rows={rows}
  columns={columns}
  pageSize={10}
  checkboxSelection
  disableSelectionOnClick
/>
</div>
</div>

        )
    }
