import React, { useState, useEffect } from "react";
import Multiselect from 'multiselect-react-dropdown';


export default function ApplyToBeMentor(){
    const initialValue = [
        { id: 0, value: " --- Select a State ---" }];

    const [stateOptions, setStateValues] = useState(initialValue);
    const [optionsArr, setOptions] = useState(initialValue);
    const [titleId, setTitle] = useState("");

useEffect(()=>{
    var auth = JSON.parse(localStorage.getItem('auth'));
    axios.get("http://localhost:8080/api/topic",auth).then(response => {
              setStateValues(response.data);
              console.log(response.data)
      }
    );
},[]);
console.log(titleId);
console.log(stateOptions.map(topic => topic.title));
const result = stateOptions.find(({title}) => title == titleId);
if(result != null)
result.subTitle.map(response => console.log(response));
    return(
        <div className="ApplyToBeMentor">
            <h1>dsa</h1>
            <select onChange={(e) => setTitle(e.target.value)}>
                {stateOptions.map(topic => <option key = {topic.id}>{topic.title}</option> )}
            </select> 
            <Multiselect
             options =  {optionsArr}/>
        </div>
        );



}
