import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import Button from '@material-ui/core/Button';
import "./Login.css";
import axios from "axios";
import GoogleLogin from 'react-google-login'
import { useAppContext } from "../libs/contextLib";
import { useHistory } from "react-router-dom";


export default function Login() {
  const history = useHistory();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  function handleSubmit(event) {

    var auth ={
        auth : {
            username:username,
            password:password
        }
      };
    localStorage.setItem('auth',JSON.stringify(auth));
    localStorage.setItem('username',username);
    localStorage.setItem('credentials', window.btoa(username + ':' + password));


    axios.get("http://localhost:8080/login",auth).then(
      response => {
        alert("işlem başarılı" + response.data );
        localStorage.setItem('role',response.data);
        localStorage.setItem("isAuthenticated",'true')
        history.push("/dashboard");
     }
).catch(error => {
    alert("işlem başarısız" + error.response );
});

}

function googleSubmit(){
  localStorage.setItem('role','user');
  localStorage.setItem("isAuthenticated",'true')
  history.push("/dashboard");

}

  return (
    <div className="Login">
      <Form onSubmit>
        <Form.Group size="lg" controlId="username">
          <Form.Label>Username</Form.Label>
          <Form.Control
            type="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </Form.Group>
        <Form.Group size="lg" controlId="password">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </Form.Group>
        <Button variant="contained" color="primary" onClick={handleSubmit} >
          Login
        </Button>
        <GoogleLogin  
          clientId = "1055889201859-fnksjqgq24iebbfj253qn169vlun128f.apps.googleusercontent.com"
          buttonText = "Login"
          cookiePolicy= {'single_host_origin'}
          onSuccess={googleSubmit}
        />       
      </Form>
    </div>
  );
}
