import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import axios from "axios";
import GoogleLogin from 'react-google-login'
import { useAppContext } from "../libs/contextLib";
import { useHistory } from "react-router-dom";
import Logout from "./Logout"

export default function UserDashboard(){
    const history = useHistory();

    function Logout(){
        localStorage.setItem("isAuthenticated","false");
       history.push("/login");
       alert("logout");
    }

    function apply(event) {

        let mentorApp ={
            "menteeid":1321,
            "details":"ben bob 5yıldır matematik mentoryüuüm "
        };

        var auth = JSON.parse(localStorage.getItem('auth'));
        axios.post("http://localhost:8080/api/mentorapplication/apply",mentorApp,auth).then(
            response => {
               alert("işlem başarılı" + response.data );

               alert(response.data);
               localStorage.setItem('role',response.data);
            }).catch(error => {
                alert("işlem başarısız" + error.response );

            });

        /*
       axios.post("http://localhost:8080/api/mentorapplication/apply",mentorApp,auth).then(
         response => {
            console.log("işlem başarılı" + response.data );
            localStorage.setItem('role',response.data);
         }
    ).catch(error => {
        alert("işlem başarısız" + error.response );

    });
      */
    }

    return(
        <div className="UserDashboard">
<h1> user </h1>
<Button onClick = {apply}>
    apply to be mentor
</Button>
<Button onClick = {Logout}>
Logout
</Button>
</div>
        )
    }
